import React, { useEffect, useState } from "react";
import API from "../api";

export default function Participants() {
  const [list, setList] = useState([]);
  const [novo, setNovo] = useState({ name: "", email: "", phone: "" });

  const load = async () => {
    const res = await API.get("/participants");
    setList(res.data);
  };

  useEffect(() => {
    load();
  }, []);

  const handleCreate = async () => {
    await API.post("/participants", novo);
    load();
  };

  const handleDelete = async (id) => {
    await API.delete(`/participants/${id}`);
    load();
  };

  return (
    <div>
      <h2>Participantes</h2>
      <input placeholder="Nome" onChange={(e) => setNovo({ ...novo, name: e.target.value })} />
      <input placeholder="Email" onChange={(e) => setNovo({ ...novo, email: e.target.value })} />
      <input placeholder="Telefone" onChange={(e) => setNovo({ ...novo, phone: e.target.value })} />
      <button onClick={handleCreate}>Criar</button>

      <ul>
        {list.map(p => (
          <li key={p.id}>
            {p.name} ({p.email}) - {p.phone}
            <button onClick={() => handleDelete(p.id)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
